import React from "react";
import SearchBar from "../SearcBar/SearchBar";
import style from "./Nav.module.css";
import { Link } from "react-router-dom";
import RickAndMortyLogo from "./Rick-And-Morty-Logo.png";

const Nav = ({ onSearch, setAccess }) => {
  const handleLogout = () => {
    setAccess(false);
  };
  return (
    <div className={style.Nav}>
      <Link to="/home">
        <img
          src={RickAndMortyLogo}
          alt="Rick and Morty"
          className={style.titleImage}
        />
      </Link>
      <input type="checkbox" id="nav-toggle" className={style.navToggle} />
      <label htmlFor="nav-toggle" className={style.navToggleLabel}>
        <span></span>
      </label>
      <nav className={style.navMenu}>
        <ul className={style.navList}>
          <li className={style.navItem}>
            <Link to="/home" className={style.navLink}>
              Home
            </Link>
          </li>
          <li className={style.navItem}>
            <Link to="/about" className={style.navLink}>
              About
            </Link>
          </li>
          <li className={style.navItem}>
            <SearchBar onSearch={onSearch} />
          </li>
          <li className={style.navItem}>
            <Link to="/favorites" className={style.navLink}>
              Favorites
            </Link>
          </li>
          <li className={style.navItem}>
            <Link to="/" className={style.navLink} onClick={handleLogout}>
              Logout
            </Link>
          </li>
        </ul>
      </nav>
    </div>
  );
};

export default Nav;
