import React, { useState } from "react";
import SearchBar from "../SearcBar/SearchBar";
import style from "./Nav.module.css";
import { Link } from "react-router-dom";
import RickAndMortyLogo from "./Rick-And-Morty-Logo.png";

const Nav = ({ onSearch, setAccess }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const handleLogout = () => {
    setAccess(false);
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <div className={style.Nav}>
      <Link to="/home">
        <img
          src={RickAndMortyLogo}
          alt="Rick and Morty"
          className={style.titleImage}
        />
      </Link>

      <div className={`${style.menu} ${isMenuOpen ? style.open : ""}`}>
        <button className={style.burgerMenu} onClick={toggleMenu}>
          <div className={style.burgerLine}></div>
          <div className={style.burgerLine}></div>
          <div className={style.burgerLine}></div>
        </button>

        <div className={style.menuItems}>
          <Link to="/home">
            <button className={style.button}>Home</button>
          </Link>
          <Link to="/about">
            <button className={style.button}>About</button>
          </Link>
          <SearchBar onSearch={onSearch} />
          <Link to="/favorites">
            <button className={style.favoritesButton}>Favorites</button>
          </Link>
          <Link to="/">
            <button className={style.logoutButton} onClick={handleLogout}>
              Logout
            </button>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Nav;
